import React, { Component } from 'react'
import './App.css'
import Keyboard from './Keyboard.js'

const SIDE = 6
const HIDDENWORDS = ["foofigthers", "nadasurf", "beatles", "batman", "avengers", "tarantino"]
const ALPHABET = 'ABCDEFGHIJKLMOPQRSTUVWXYZ'
const VISUAL_PAUSE_MSECS = 750


class App extends Component {
  state = {
    hiddenWords: [],
    onPlayWord: [],
    Letters: this.generateKeyboards(),
    guesses: 0,
  }

  generateKeyboards() {
    const result = []
    const size = SIDE * SIDE
    const candidates = ALPHABET
    while (result.length < size) {
      const keyboard = candidates.pop()
      result.push(keyboard, keyboard)
    }
    return (result)
  }

  getFeedbackForKeyboard(index) {
    const { currentPair, matchedKeyboardIndices } = this.state
    const indexMatched = matchedKeyboardIndices.includes(index)

    if (currentPair.length < 2) {
      return indexMatched || index === currentPair[0] ? 'visible' : 'hidden'
    }

    if (currentPair.includes(index)) {
      return indexMatched ? 'justMatched' : 'justMismatched'
    }

    return indexMatched ? 'visible' : 'hidden'
  }

  // Arrow fx for binding
handleKeyboardClick = index => {
  const { currentPair } = this.state

  if (currentPair.length === 2) {
    return
  }

  if (currentPair.length === 0) {
    this.setState({ currentPair: [index] })
    return
  }

  this.handleNewPairClosedBy(index)
}
handleNewPairClosedBy(index) {
  const { Letters, currentPair, guesses, matchedKeyboardIndices } = this.state

  const newPair = [currentPair[0], index]
  const newGuesses = guesses + 1
  const matched = Letters[newPair[0]] === Letters[newPair[1]]
  this.setState({ currentPair: newPair, guesses: newGuesses })
  if (matched) {
    this.setState({ matchedKeyboardIndices: [...matchedKeyboardIndices, ...newPair] })
  }
  setTimeout(() => this.setState({ currentPair: [] }), VISUAL_PAUSE_MSECS)
}

  render() {
    const { Letters, guesses } = this.state
    return (
     <div className="game">
      <h1> Devine la pop Culture</h1>
      {Letters.map((Keyboard, index) =>(
          <Keyboard
            keyboard={keyboard}
            feedback={this.getFeedbackForKeyboard(index)}
            key={index}
            index={index}
            onClick={this.handleKeyboardClick }
          />
        ))}
     </div>
   )
  }
}

export default App
