import React, { Component } from 'react'
import './App.css'

class App extends Component {
  state = {
    hiddenWords: ["foofigthers", "nadasurf", "beatles", "batman", "avengers", "tarantino"],
    onPlayWord: null,
    alphabet: "ABCDEFGHIJKLMOPQRSTUVWXYZ".toLocaleLowerCase().split(''),
  }
  render() {
    return (
     <div>
      <h1> Devine la pop Culture</h1>
      <p> {this.state.hiddenWords} </p>
      <p> {this.state.alphabet} </p>
     </div>
   )
  }
}

export default App
