import React, { Component } from 'react'
import './App.css'
import shuffle from 'lodash.shuffle'

import Keyboard from './Keyboard.js'

const SIDE = 1
const HIDDENWORDS = ["foofigthers", "nadasurf", "beatles", "batman", "avengers", "tarantino"]
const ALPHABET = 'ABCDEFGHIJKLMOPQRSTUVWXYZ'
const VISUAL_PAUSE_MSECS = 750


class App extends Component {
  state = {
    hiddenWords: [],
    onPlayWord: [],
    Letters: this.generateKeyboards(),
    HidWord: this.generateWords(),
    guesses: 0,
    matchedCardIndices: [],
  }

  generateKeyboards() {
    const result = []
    const size = SIDE * SIDE
    const candidates = ALPHABET
    while (result.length < size) {
      const keyboard = candidates
      result.push(keyboard, keyboard)
    }
    return (result)
  }

  generateWords() {
    const result = []
    const size = SIDE * SIDE
    const wordz = shuffle(HIDDENWORDS)
    while (result.length < size) {
      const word = wordz.pop()
      result.push(word, word)
    }
    return shuffle(result)
  }


  render() {
    const { Letters, guesses } = this.state
    return (
     <div className="game">
      <h1> Devine la pop Culture</h1>
      <div>
        {shuffle(HIDDENWORDS.slice(3, 4))}
      </div>
      <div>
        {ALPHABET}
      </div>
     </div>
   )
  }
}

export default App
